// https://www.desmos.com/calculator/l3m2wli93t

// Created by Seyed Morteza Kamali

//you can use this periodic delay in your animation
//for example, your animation run after 2 second


#define period 2.0 //you can change the period

#define Delay 2.0 //you can change the delay

#define PI 3.14

float periodicShift(float x) {
float shift = (period-1.)*PI+Delay;

return fract(x/(PI+shift))*(PI+shift);    
//return x-(PI+shift)*floor(x/(PI+shift)); 
}

float f(float x) {
float p = periodicShift(x); 
return sin(clamp(p,0.,period*PI));
}

void mainImage( out vec4 O, vec2 U )
{  
    U /= iResolution.xy;
    float z = f(6.28*(U.x+iTime) );
    z = .5+.2*z- U.y ; 
    O = vec4( smoothstep(2.,0.,abs(z)/fwidth(z)) );
    //O = vec4(f(iTime));
}
