//return 3d honeycomb, distance to closest "white tile" of checkerboard tiling
float cells(vec3 p){p=fract(p/2.)*2.;p=min(p,2.-p );    
 return min(length(p),length(p-1.));}

void mainImage( out vec4 O, in vec2 fragCoord){O=vec4(1);
 vec2 u = fragCoord.xy/iResolution.xy;
 u.x*=iResolution.x/iResolution.y;
 vec3 u3=vec3(u*9.,iTime+.5);
 float r=cells(u3);//distance to honeycomb center
 vec3 a=step(.5,fract(u3*.5-.25));//color coded 3d coordinates
 //r=fract(r*5.); 
 r=smoothstep(-.01,.01,r-.875);
 a=a*.5+.5;//mix with 50% grey                        
 O.rgb=1.-a*r;
}

