float h(vec3 p)//Dave_Hoskins https://www.shadertoy.com/view/4djSRW
{
	p=fract(p*.103);
    p+=dot(p,p.yzx+19.2);
    return fract((p.x+p.y)*p.z);
}
vec4 f(vec2 x){
    vec4 a=vec4(0);
    float s=1.;
    for(int n=0;n<5;n++){
        a+=texture(iChannel0,s*x)/s;
        s*=2.;
    }
    return a*.65;
}
void mainImage(out vec4 o,vec2 i){
    i/=iResolution.xy;
    o=smoothstep(.9,1.,f(i));
    //(length(o)>.7)?o+=h(vec3(i,iTime)):o=vec4(0.); //twinkle
    //(length(o)>.7)?o+=texture(iChannel0, i+iTime):o=vec4(0.0); //not working
    o+=(dot(o,o)>.5)?h(vec3(i,iTime)):0.; //Fabrice's twinkle
}
