// This illustrates the precision problem of WebGL.

#define MAX_STEPS 512

// Change these to zoom on various points
#define ZOOMX 0.001643721971153
#define ZOOMY 0.822467633298876

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy * 5. - 2.5)
				/ pow(2., iTime) + vec2(ZOOMX, ZOOMY);

	vec2 z = vec2(0.);

	float i;

	for(int c = 0; c < MAX_STEPS; c++) // lol
	{
		i = float(c);
		if(length(z) > 2.) break;

		z = vec2(z.x * z.x - z.y * z.y, z.x * z.y * 2.) + uv;
	}

	fragColor = vec4(float(mod(i, 64.)) / 64.,
						float(mod(i, 32.)) / 32.,
						float(mod(i, 16.)) / 16.,
						1.);
}
