#define iteration 20
#define speed 41.



float cymatic(vec2 pos,float freq)
{	
	float zoom = sqrt(freq) ;
	pos *= (zoom);

	float v = 0.;
	float PointsDist = length((iMouse.xy  / iResolution.xy) - vec2(0.5,0.5)) *  zoom * 10.;

	float LastWave;


	for(int i = 0;i < iteration;i++)
	{				
		float Iterator = float(iteration);
		float floatI = float(i);

		float Angle = 	float(floatI/Iterator) * (3.14168 * 2.) ;	
		vec2 PointFreq = vec2(sin(Angle),cos(Angle)) * PointsDist ;


		PointFreq.y = clamp(PointFreq.y , -PointsDist + (PointsDist/3.),PointsDist - (PointsDist/3.) );
		PointFreq.x = clamp(PointFreq.x , -PointsDist + (PointsDist/3.),PointsDist - (PointsDist/3.) );


		//float wave = ((cos(  sqrt(length(pos - PointFreq) * (freq ) )   ))) * (1./float(Iterator));

		float wave = (sin((length(pos - PointFreq) ) )  * 0.001);
		//wave = clamp(wave,0.,1.);



		v -= (wave * .012);

	}

	v *= ((  (length(pos * PointsDist) * (PointsDist / 8. ) )   )    );


	return sin(v );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(0.5,0.5);
	float freq = iTime * speed;
	freq = 1512. + freq;

	float valx = cymatic(uv,freq * 1.);
    float valy = cymatic(uv,freq * 1.001);
    float valz = cymatic(uv,freq * 1.002);

	fragColor = vec4(valx,valy,valz,1.0);
}
