#define M_PI 3.14159265358979323846
#define ITERS 100

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 c = fragCoord.xy / (iResolution.xy / 2.5) - vec2(1.5, 1.25);
	c.x *= iResolution.x / iResolution.y;
	vec2 a = c, ar = vec2(0.0, 0.0);
	vec3 color = vec3(1.0, 0.9, 0.8);
	int n = ITERS;

	for (int i = 0; i < ITERS; ++i) {
		float am2 = dot(a, a);
		ar += vec2(a.x, -a.y) / am2;
		if (am2 > 4.0) {
			color = vec3(0.8, 0.9, 1.0);
			n = i;
			break;
		}
		a = vec2(a.x * a.x - a.y * a.y, 2.0 * a.x * a.y);
   	    a += c;
	}

	ar /= float(n + 1);
	a = vec2(ar.x, -ar.y) / dot(ar, ar);
	float g = fract(log(length(a)) + (atan(a.y, a.x) / (2.0 * M_PI)) - iTime);
	fragColor = vec4(color * g, 1.0);
}
