#define PI 3.14

vec3 xor(in vec2 uv) {
    vec2 p = 256. * uv;
    float x = 0.;
    for(int i=0; i < 8; i++) {
        vec2 a = floor(p);
        x += mod(a.x + a.y, 2.);
        p /= 2.;
        x /= 2.;
    }
    return vec3(x,x,x);
}

//frequency default is 1.
//offset default is 0.
vec3 sinX(in vec2 uv, in float frequency, in float offset) {
    vec2 p = 256. * uv;
    float x = sin((p.x + offset) * frequency * PI);
    return vec3(x,x,x);
}

//frequency default is 1.
//offset default is 0.
vec3 sinY(in vec2 uv, in float frequency, in float offset) {
    vec2 p = 256. * uv;
    float x = sin((p.y + offset) * frequency * PI);
    return vec3(x,x,x);
}

vec3 noise(in vec2 uv) {
    vec3 q = vec3( dot(uv,vec2(127.1,311.7)), 
				   dot(uv,vec2(269.5,183.3)), 
				   dot(uv,vec2(419.2,371.9)) );
	return fract(sin(q)*43758.5453);
}

vec3 tex1(in vec2 uv) {
    vec3 x = vec3(0.);
    x += sinX(uv, 0.066, 0.);
    x += sinY(uv, 0.066, 0.);
    x *= sinX(uv, 0.044, 32.) * vec3(2.);
    x *= sinY(uv, 0.044, 16.) * vec3(2.);
    x -= vec3(0.5, 2., 4.);

    return x;
}

vec3 tex2(in vec2 uv) {
    vec3 x = vec3(0.);
    x += sinX(uv, 0.05, 0.);
    x *= sinX(uv, 0.08, 0.);
    x += sinY(uv, 0.05, 0.);
    x *= sinY(uv, 0.08, 0.);
    x /= vec3(1., 2., 1.);
    x += sinX(uv, 0.003, 0.) * vec3(.5, 0., 0.);
    return x;
}

vec3 tex3(in vec2 uv) {
    vec3 x = vec3(0.);
    x += sinX(uv, 0.01, 0.);
    x *= sinY(uv, 0.0075, 0.);
    x += sinX(uv, 0.0225, 0.);
    x *= sinY(uv, 0.015, 0.);
    x += noise(uv) * vec3(0.1, 0.1, 0.3);
    return x;
}

vec3 tex4(in vec2 uv) {
    vec3 x = vec3(0.);
    x += xor(uv) * vec3(1., .5, .7);
    x += sinX(uv, 0.004, 0.) * vec3(.25, 0., 0.);
    x -= sinY(uv, 0.004, 0.) * vec3(.25, 0., 0.);
    x += sinX(uv, 0.0065, 0.) * vec3(.1, .5, .2);
    x += sinY(uv, 0.0065, 0.) * vec3(0., .4, .5);
    x += noise(uv) * vec3(.1, .1, .2);
    return x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.y = 1.-uv.y;

    // Examples
    vec3 x = tex1(uv);
    //vec3 x = tex2(uv);
    //vec3 x = tex3(uv);
    //vec3 x = tex4(uv);

	fragColor = vec4(x,1.0);
}
