#define PI 3.14159265358
#define TWO_PI 6.28318530716

float rand(vec2 uv)
{
    //return fract(sin(dot(uv, vec2(12.9898,78.233)))*10000.*iTime);
	//return fract(sin(dot(uv, iMouse.xy))*10000.);
	//return (fract(sin(dot(uv, vec2(12., 70.)))*100000.));
    return (fract(sin(dot(uv, vec2(12., 70.)))*43758.5453123));
}

// Value noise
float noise(in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    // Smooth Interpolation

    // Cubic Hermine Curve.  Same as SmoothStep()
    vec2 u = f*f*(3.0-2.0*f);
    // u = smoothstep(0.,1.,f);

    // Mix 4 coorners porcentages
    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}

// Gradient noise
vec2 hash( vec2 x )  // replace this by something better
{
    const vec2 k = vec2( 0.3183099, 0.3678794 );
    x = x*k + k.yx;
    return -1.0 + 2.0*fract( 16.0 * k*fract( x.x*x.y*(x.x+x.y)) );
}

float gnoise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( dot( hash( i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ), 
                     dot( hash( i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( hash( i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ), 
                     dot( hash( i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}

// Simplex noise
vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }

float snoise(vec2 v) {
    const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                        0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                        -0.577350269189626,  // -1.0 + 2.0 * C.x
                        0.024390243902439); // 1.0 / 41.0
    vec2 i  = floor(v + dot(v, C.yy) );
    vec2 x0 = v -   i + dot(i, C.xx);
    vec2 i1;
    i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
    vec4 x12 = x0.xyxy + C.xxzz;
    x12.xy -= i1;
    i = mod289(i); // Avoid truncation effects in permutation
    vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
        + i.x + vec3(0.0, i1.x, 1.0 ));

    vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
    m = m*m ;
    m = m*m ;
    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;
    m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
    vec3 g;
    g.x  = a0.x  * x0.x  + h.x  * x0.y;
    g.yz = a0.yz * x12.xz + h.yz * x12.yw;
    return 130.0 * dot(m, g);
}

// ROTATE AND SCALE FUNCTIONS-----------------------------------------

mat2 rotate2d(float angle)
{
    return mat2(cos(angle),-sin(angle), sin(angle), cos(angle));
}

mat2 scale2d(vec2 value)
{
    return mat2(value.x,0,0,value.y);
}

//SHAPES------------------------------------------------------------
vec3 polarShape(vec2 uv,float dist, float angle, float radius, float p,vec3 colour)
{
    float value = 1.0 - step(radius + p,dist);

    return vec3(value) * colour;
}

vec3 circle(vec2 uv, vec2 pos, float radius,vec3 colour, float Noise)
{
    float value = step(distance(pos,uv), radius);

    value *= Noise;

    return vec3(value) * colour;
}

//Main Code------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = 1.5;
    uv.x *= ratio;

    vec2 pos = vec2(0.5 * ratio,0.5);

    // Noise
    float a = noise(uv * 11. + iTime) * 0.1;
    vec3 color = vec3(0.);
    float value = 0.;

    // Snow
    float r = snoise(uv * 2. + iTime * 0.5) * ratio;
	value = step(0.0 + r ,uv.x) - step(0.4 + r,uv.x);
    color = vec3(value);

    // Circle Surround the person
    color = mix(color, circle(uv,vec2(0.5*ratio,0.5),0.4,vec3(.2,.4,.2),1.), 0.8);

    // Head
    uv -= pos;
    color += circle(uv,vec2(0.,0.2),0.1,vec3(0.2,0.2,1),r);
    color -= circle(uv,vec2(0.04,0.23),0.02,vec3(0.3,2,.2),2.);

    // Body
	uv += pos;
    value = step(0.62 + a,uv.x) - step(0.85,uv.x);
    value *= step(0.25,uv.y) - step(0.55,uv.y);
    value *= r;
    color -= vec3(value) * vec3(0,1,0);

	// Horizontal noise
    value = step(0.54,uv.x) - step(0.84,uv.x);
    value *= step(0.48 + a,uv.y) - step(0.57 + a,uv.y);
    value *= r;
	color -= vec3(value) * vec3(0.1,0.3,.4);

    // Lower body
    value = step(0.76,uv.x)- step(0.79,uv.x);
    value *= step(0.15,uv.y) - step(0.25,uv.y);
    value *= r;
    color += vec3(value) * vec3(0.2,0.2,1);

	fragColor = vec4(color,1.0);
}
