mat2 rotate(float a) {
	return mat2(cos(a), -sin(a), sin(a), cos(a));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (fragCoord.xy -0.5 * iResolution.xy) / iResolution.x * rotate(iTime / 3.0);
    /*
    bool x = mod(uv.x, 0.1) < 0.05;
    bool y = mod(uv.y, 0.1) < 0.05;
    bool c = x != y;
*/
    float x = mod(uv.x, 0.1);
    float y = mod(uv.y, 0.1);
    //float c = mix(x, y, 0.5);
    float xx = smoothstep(0.029, 0.031, x) * (1.0 - smoothstep(0.079, 0.081, x));
    float yy = smoothstep(0.029, 0.031, y) * (1.0 - smoothstep(0.079, 0.081, y));

    float c = mix(xx, yy, 0.5);


    vec3 col = vec3(c);
	fragColor = vec4(col, 1.0);
}
