// https://soundcloud.com/prophazze/morning-high-tech-full-on-psytrance-mix-by-prophaze
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = fragCoord.xy/iResolution.xy-0.5;


    float tt = -.015 * (iTime + iDate.g * iDate.b + 71.);

     mat2 r = mat2(cos(tt/1.002),sin(tt/1.003),-sin(tt/1.005),cos(tt/1.007));
    float c0 = texture(iChannel0, vec2(0.0,0.0)).x;
   float c1 = texture(iChannel0, vec2(0.0,0.0)).y;

    vec4 p0 = vec4(pos, fragCoord.yy);
    vec4 p = abs(0.-8.*(p0) ), c=p*.0;
    p.yx *= r;
    for (int d=2; d<7; d++) {
        p -= c0/3.*float(d);
        for (int i=0;i<3;i++) p.yx=r*(p.yx-
           sign(p.yx)*
           vec2(c1*cos(sin(c1/(c0+0.01))),1.5*sin(sin(3.14*tt))));
        c += .063*p;
    }
    fragColor = c;
    //fragColor = clamp(fragColor/1.3,0.0,1.0);
}
