void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 blue = vec3(0.04, 0.005, 0.912);
    vec3 blue2 = vec3(0.045, 0.02, 0.912);
    vec3 ground = vec3(0.03, 0.02, 0.1);

    vec3 color = mix(blue2, ground, uv.y);

    float wave = sin(uv.x * 32.0 + iTime*2.0) + sin(uv.x * 23.0 + iTime) + sin(uv.x *25.0 + iTime);
	float wave2 = sin(uv.x * 43.0 + iTime) + sin(uv.x * 103.0 + iTime) + sin(uv.x *25.0 + iTime);

    float tOff = sin(iTime * 0.3);
    color = mix(ground, color, wave/30.0 * tOff);

    color = color / smoothstep(0.1, 0.3, uv.y - 0.23 + wave/35.0)*2.0;
    color = color / smoothstep(0.1, 0.5, uv.y - 0.23 + wave2/90.0);

    fragColor = vec4(color,1.0);
}
