void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ((1220.0 + iTime) / 24.0) * ((fragCoord.xy - (iResolution.xy / 2.0)) / iResolution.y);

    float r = mod(iTime / 100.0 + acos(-1.0) / 4.0, acos(-1.0)*2.0);
    mat2 rot = mat2(cos(r), -sin(r), sin(r), cos(r));
    //uv = uv * rot;

    if(uv.x == 0.0) {
      r = 1.0;
    } else {
      r = atan(uv.y / uv.x) * 1.0;
    }
    rot = mat2(cos(r), -sin(r), sin(r), cos(r));
    uv = uv * rot;

    float value = (uv.x * sign(uv.y) + uv.y * sign(uv.x)) * sin(uv.x) * sin(uv.y);
    float color = sin(value) * 3.0;
    color = color * sign(uv.x) * sign(uv.y);

    float low = abs(color);
    float med = abs(color) - 1.0;
    float high = abs(color) - 2.0;

    fragColor = vec4(high, high, low,1.0);
}
