#define L length
void mainImage( out vec4 c, vec2 i ){
    vec2 p,R=iResolution.xy;
	p = i.xy / R-.5;
	p.x *= R.x / R.y;

	float j = clamp(floor(L(p)*36.17+.5),1.,17.),r,t;

	r = j / 36.17;

	t = iTime * (18.-j) *.4189;

	c=vec4(smoothstep(0., 2./R.y, min(abs(L(p)-r),L(r * vec2(-sin(t),-cos(t))-p)-.006)));
}
