#define PI 3.141592653589793238462643383279502884197169399375105820974

vec4 over (vec4 c1, vec4 c2)
{
    float w = mix(c2.w, 1.0, c1.w);
    return vec4( mix(c2.xyz*c2.w, c1.xyz, c1.w)/w, w );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = -(fragCoord - 0.5*iResolution.xy)/iResolution.y;
	float time = iTime;
    vec3 resolution = iResolution;
    float st = ( sin(time) + 1.0 )/2.0;
    float stCam = ( sin(time*0.3) + 1.0 )/1.0 + 1.0/24.0;

    uv = mod(uv*stCam*24.0 + 1.0, 2.0) - 1.0;

    float at = atan(uv.x, uv.y);

    float r1 = ( cos(10.0*at + time*2.0)*sin(41.0*at - time*3.0) )/100.0 + 0.36;
    float r2 = ( cos(100.0*at + time)*sin(40.0*at - time*0.7*3.0) )/100.0 + 0.38;

    float a1 = smoothstep( r1 + 0.06, r1, length(uv) );
    float a2 = smoothstep( r2 + 0.2 + stCam/3.0, r2, length(uv) );

    vec4 col = over( vec4( vec3(1.0, 0.8, 0.4), a1*0.7 ), vec4( vec3(1.0, 0.5, 0.2), a2*1.0 ) );
    col = over( col, vec4( vec3(0.0), 1.0 ) );
    fragColor = col;
}
