void mainImage(out vec4 c, vec2 u)
{
    c += .62+.42*texelFetch(iChannel0, ivec2(u),0);
    //c += .33*texelFetch(iChannel1, ivec2(u),0);
}

vec4 T(vec2 x) {
   return texture(iChannel0, fract((x)/iResolution.xy));
}

void mainImage(out vec4 c, vec2 fragCoord)
{   

    float initialScale = 1.0 / 1e6;

    c = fragCoord.xxyx * initialScale;

    float initial_t = 1.;
    float t_threshold = 1e2;
    float growth_factor = .75;

    float color_scale_A = 1.0/3.0;
    float color_scale_B = 0.3;
    float color_scale_C = 3.0; // OOOOOOHHHHHHH PLAY WITH ME

    float mouse_influence = 1.5;

    float pulse_influence = 0.01;
    float pulse_baseline = 0.025;


    for(float t=initial_t; t<t_threshold; t+= (t * growth_factor)) {
    	c += c.gbar * color_scale_A - c*color_scale_B + color_scale_C*T(fragCoord-c.wz*t);
        c += mouse_influence*texelFetch(iChannel1, ivec2(fragCoord),0);
    }
	//c = mix(T(fragCoord+0.5*c.xy), cos(c), .025);
	c = mix(T(fragCoord+0.5*c.xy), cos(c), sin(iTime*1.)*pulse_influence + pulse_baseline);
}

float circleSDF(vec2 st) {
 	return length(st);   
}

float fill(float x, float size) {
 	return pow(1.-smoothstep(0.0, size, x), 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 st = fragCoord.xy/iResolution.y;
    vec3 color = vec3(0.);



    color += fill(circleSDF(st-iMouse.xy/iResolution.y), .2);
    fragColor = vec4(color,0.0);
}
