vec3 COLORS[ 8 ];

vec3 getData(int id) {
    for (int i=0; i<8; i++) {
        if (i == id) return COLORS[i];
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    COLORS[0] = vec3(1,0,0);
    COLORS[1] = vec3(0,1,0);
    COLORS[2] = vec3(0,0,1);
    COLORS[3] = vec3(1,1,1);
    int colorMax = 4;
    float speed = 4.0;

    int color0 = int(mod(iTime / speed, float(colorMax)));
    int color1 = color0 + 1;
    if(color1 >= colorMax) {
        color1 = 0;
    }

    float perc = mod(iTime, float(colorMax) ) / speed;

    vec3 col0 = getData(color0);
    vec3 col1 = getData(color1);

    float r = col0.r + (col0.r - col1.r) * perc;
    float g = col0.g + (col0.g - col1.g) * perc;
    float b = col0.b + (col0.b - col1.b) * perc;

    vec3 color = col0 + (col1 - col0) * perc;

	fragColor = vec4(color, 1.0);
}
