float noise2d(vec2 p) {
	return fract(sin(dot(p ,vec2(17.9898,78.233))) * 456367.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 p = fragCoord.xy / iResolution.xy;

	float a = 0.0;

	const float size = 40.0;

	for(float i = 0.; i < 1.; i+=1./size) {
		float t = size - i * size;
		p.x += iTime * i * 0.01;
		float px = floor((p.x + noise2d(vec2(i))) * t);
		float top = i * mix(i / size * sin(3.14 * 0.5 * iTime * 0.5), 0.7, 1.2);
		if(p.y + top < noise2d(vec2(px)))
			a = 1.0 * sin(3.14 * i);
	}

	fragColor = vec4(vec3(a), 1.0);
}
