#define c30 0.86602540378
#define hm  1.33333333333
#define grid 16.0
#define smooth 0.02
#define timeScale 0.2


vec2 hex(vec2 v){
	float yc = abs(mod(v.x + floor((v.y*hm + 0.5))*(c30 / 2.0),c30) / c30 - 0.5);
	float y = floor(v.y*hm + yc);
	float x = floor(v.x/c30 + 0.5 + fract(y /  2.0))- fract(y / 2.0);
	return vec2(x*c30,y /hm + 1.0/(hm * 4.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float wave =  texture( iChannel0, vec2(0.0,1.0)).x;
	float wave2 = texture( iChannel0, vec2(0.0,.1)).x;

	vec2 uv = fragCoord.xy  / vec2(iResolution.y,iResolution.y);
  	uv.x -= .5;
    float rt = iTime * timeScale;
    vec2 gv = hex(uv*grid)/grid;
    vec2 mv = gv - 0.5;
    vec2 rv = vec2(((atan(mv.x,mv.y)/3.141526) + cos(iTime + wave2)) / 2.0, length(mv * sin(iTime + wave2)));
    float fv = fract((rv.x- rt) * cos(gv.x /20.0) * 2.0 + rv.y * 2.0);

    float dist = length(uv - gv) * grid;
    float dist2 = 1.0 - smoothstep(-smooth,0.0,dist - c30/2.);
    dist += fv + wave2;
    dist += cos(wave / 2.);
    dist = smoothstep(0.5,0.5 + smooth,fract(dist));
    float finCol = float(0.5);
    finCol = mix(finCol, dist, dist2);
	fragColor = vec4(finCol) + vec4(cos(iTime + dist2 + wave2) + wave2, sin(fv * wave) * .5, sin(uv.x * 3.),  1.0);
}
