const float scale = 0.1;

float eye(vec2 pos,vec2 offset){
	float o = 0.0;

	vec2 uv = pos + offset;

	float d = (uv.x*uv.x) + (uv.y * uv.y);

	o = smoothstep(d,scale*.5,4.);

	//pupil
	vec2 m = -1.0 + 2.0 * iMouse.xy / iResolution.xy;
	m.x *= iResolution.x/iResolution.y;
	m+=offset;


	float dd = length(m);

	vec2 norm = normalize(m);
 	norm*= min(dd,scale);

	vec2 pupPos = uv - norm;

	float d2 = (pupPos.x*pupPos.x) + (pupPos.y * pupPos.y);
	if(d2 < 0.1*scale)	{
		o = 0.0;
	}

	return o;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;



	float o = eye(uv,vec2(-0.3,0.0));
	o += eye(uv,vec2(0.3,0.0));
	fragColor = vec4(o,o,o,1.0);
}
