float sq(float x) { return x*x; }

float eyeOuter(vec2 v, vec2 p) {
	float sum = sq(p.x - v.x) + sq(p.y - v.y);

	return (sum < 0.06) ? 1.0 - sum*16.0 : 0.0;
}

float eyeInner(vec2 v, vec2 p) {
    vec2 mouse = iMouse.xy / iResolution.xy;
	vec2 vm = vec2(mouse - 0.5 - v*0.42);
	float l = length(vm);

	vm *= 0.1/l;

	float sum = sq(p.x-v.x-vm.x)+sq(p.y-v.y-vm.y);

	//return (sum < 0.02) ? 1.0 - eyeOuter(v, p) : 0.0;
	return (sum < 0.02) ? 1.0-sum*50.0 : 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = ( fragCoord.xy / iResolution.xy );

	p.x = (p.x-0.5)*1.8;
	p.y = (p.y-0.5);

	float val = 0.0;
	val += eyeOuter(vec2(-0.3, 0.1), p);
	val += eyeOuter(vec2(0.3, 0.1), p);
	val -= eyeInner(vec2(-0.3, 0.1), p);
	val -= eyeInner(vec2(0.3, 0.1), p);
	vec3 clr = vec3(val, val, val);

	fragColor = vec4(clr, 1.0);
}
