vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 coord = fragCoord.xy / iResolution.xy;
	//coord.y = 1.0 - coord.y;

	vec4 modulator = texture(iChannel0, coord);

	//vec4 orig = texture(iChannel1, coord + ((modulator * 0.2) - 0.1).xy);

	vec3 color = vec3(0.0, 0.0, 0.0);
	color = rgb2hsv(color);
	color.x = mod(((modulator.x + modulator.y + modulator.z) * 0.7) + (iTime*5.5), 1.0);
	color.y = 1.0;
	color.z = 1.0;
	color = hsv2rgb(color);

	fragColor = mix(vec4(color, 1.0), modulator, 0.0);
}
