vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),sin(_angle),cos(_angle)) * _st;
    return _st + 0.5;
}
float e = 7.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = rotate2D(fragCoord.xy / iResolution.y, iTime * -0.05);
    st.x += step(1., mod(st.y * e, 2.0)) * 0.5;
    float g = (step(1., mod(st.x * e, 2.0)) - 0.5) * iTime * 6.5 ;  
    st = fract(st * e) - 0.5;
    float r = clamp(0.46, 0.37, 0.42 + 0.12 * sin( atan(st.x,st.y) * 7.0  +  g));
    fragColor = vec4(smoothstep(r,r + 0.01,length(st)));
}
