#define iterations 42

vec4 black = vec4(0.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

float m = iResolution.x / iResolution.y;
float t = iTime*0.001;

    vec2 p = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
	p.x *= m;
	vec2 c = p;
	c.x += sin(p.y+iTime/10.6)*0.2;
	c.y += -cos(p.x+iTime/10.4)*0.2;

	c *= 1.5+sin(iTime*0.017)*0.5;

	float iter = 0.0;
	vec4 color = black;	
	float fy = sin(0.05*p.y+iTime*0.97);
	for (int i = 0; i < iterations; i++) {
		vec4 g = texture(iChannel0, c);
		color += g*float(i)/float(iterations);
		float phi = atan(c.y, c.x) + t*iter*fy*0.002;
		float r = dot(c,c);
		if (r < (1.2+sin(0.3+p.x+iTime/1.3))*55.0) {
    		c.x = ((cos(2.05*phi))/r) + p.x;
    		c.y = (-sin(1.93*phi)/r) + p.y;

			c *= 0.5+cos(sin(p.y*0.013)+iTime*0.3)*.2;

    		iter++;
		}
		color *=1.1;
		color = vec4(
			      (color.r*float(float(iter)/float(iterations)))*sin(cos(p.y*t)*p.x),
			      0.5*float(iter/float(iterations))/color.g,
			      color.b*float(iter/float(iterations)),1.0);

	}
	color.r *= cos(1.0+p.y+iTime/1.14)*8.0 ;	
	color.g *= sin(p.x+iTime/1.3)*8.0;
	color.b *= 0.5+sin(1.5*p.y+iTime*1.97)*3.5;

	fragColor = vec4(color / float(iterations) + max(0.75 - iter / float(iterations), 0.0));
}
