#define PI 3.1415
#define speed 0.1
#define mpow 0.3

void mainImage( out vec4 C, in vec2 uv )
{
    //white dot


    //black half

    //power uv, panning noise

	uv = uv.xy / iResolution.xy;

    //float moving = sin(iDate.w*0.5)*0.4+0.5 + texture(iChannel0, uv + iDate.w*0.2).x;
    float moving = sin(iDate.w*0.1)*0.4+0.5 + texture(iChannel0, vec2(iDate.w*0.02)).x*0.15;

    float mask = 1.-floor(uv.x+0.5);
    float target = 1.-floor(64.0*distance(vec2(0.9+sin(iDate.w*0.33)*0.05,moving),uv));

    vec2 newuv = vec2(sin(uv.y*PI)*0.2+uv.x, uv.y);

    float noise = texture(iChannel0, vec2(uv.x+iDate.w*0.1, uv.y+sin(iDate.w*0.5)*0.5)).x;
    float noise2 = texture(iChannel0, vec2(uv.x-iDate.w*0.05, uv.y-sin(iDate.w*0.37)*0.5)).x; 
    float distortion = noise2*noise*0.3*pow((1.-uv.x*2.0),3.0);

    //vec3 map = texture(iChannel1, distortion + vec2(pow(newuv.x, 2.0),newuv.y)+vec2(iDate.w*speed, 0.0)).xyz;
    vec3 map = texture(iChannel1, distortion + vec2(pow(newuv.x, mpow),newuv.y)+vec2(iDate.w*speed, 0.0)).xyz;

    vec3 test = texture(iChannel1, newuv).xyz;

	C = max(mask, target)*max(vec4(map, 1.0),target);
}

