void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 new_color = vec3(fragCoord.xy,1.0) * mat3(1.,1.,1.0,2.,0.,1.0,0.,0.,1.);
	vec2 uv = new_color.xy / iResolution.xy;
    vec3 xyz = vec3(uv,1.0);

    float x = xyz.x;
    float y = xyz.y;

    float color = cos(sqrt(x) * iTime) + cos(sqrt(y) * iTime);

	fragColor = vec4(color, sqrt(color), atan(pow(color, 2.0)), 1.0);
}
