// Thnx FabriceNeyret2!
// Source: https://www.shadertoy.com/view/ltcXzs
vec4 char(vec2 p, int C) {
    if (p.x<0.|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
    return textureGrad( iChannel0, p/16. + fract( vec2(C, 15-C/16) / 16. ) , 
                       dFdx(p/16.),dFdy(p/16.) );
}

void chr(in int a, in vec3 tint, out float ch, inout vec3 fragColor, inout vec2 uv) {
    ch = char(uv, a).x;
    fragColor = mix(fragColor, tint, ch);
    uv.x -= .5;
}
#define ic(a) chr(a, tint, ch, fragColor.xyz, uv);

float dstRect(vec2 p, vec2 pos, vec2 box) {
    return length(max(abs(pos-p)-box, 0.));
}
float dstRoundRect(vec2 p, vec2 pos, vec2 box, float r) {
    return dstRect(p,pos,box)-r;
}
float dstCircle(vec2 p, vec2 pos, float r) {
    return length(pos-p)-r;
}

float dstUnion(float a, float b) {
    return min(a, b);
}
float dstMax(float a, float b) {
    return max(-a,b);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
	fragColor = vec4(1.,0.,0.,1.);

    // Draw logo
    float y = uv.x < 0. ? 0. : sin(1.+iTime*5.)*.1*(1.-smoothstep(1.,1.05,iTime));
    float d = dstRoundRect(uv, vec2(0.,.1+y), vec2(.15), .1);

    if(uv.x < 0.) {
        d = dstMax(dstRoundRect(uv, vec2(-.03,.1), vec2(.1,.13), .09), d);
        d = dstUnion(dstRect(uv, vec2(0., .1), vec2(.07,.22)), d);
        d = dstUnion(dstCircle(uv, vec2(-.14, .18), .04), d);
    } else {
        d = dstMax(dstCircle(uv, vec2(.13,y+.05), .04), d);
    }

    if(d <= 0. && (uv.x < -.03 || uv.x > .03)) {
        fragColor.yz = vec2(1.);
    }

    // Draw text
    float ch = 0.;
    vec3 tint = vec3(1., 1., 1.);
    uv *= 6.5; uv.x += 2.3; uv.y += 2.1;
    ic(78);
    ic(73);
    ic(78);
    ic(84);
    ic(69);
    ic(78);
    ic(68);
    ic(79);
    uv.x += 3.5; uv.y += .75;
    ic(83);
    ic(87);
    ic(73);
    ic(84);
    ic(67);
    ic(72);
}
