void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 deadColor = vec4(0.2, 0.35, 0.25, 1.0);
    vec4 aliveColor = vec4(0.9, 1.0, 0.25, 1.0);
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = mix(deadColor, aliveColor, texture(iChannel0, uv).x);
}

float hash(float n) { 
    return fract(sin(n) * 1e4); 
}

float hash(vec2 p) { 
    return fract(1e4 * sin(17.0 * p.x + p.y * 0.1) * (0.1 + abs(sin(p.y * 13.0 + p.x)))); 
}

float noise(float x) {
	float i = floor(x);
	float f = fract(x);
	float u = f * f * (3.0 - 2.0 * f);
	return mix(hash(i), hash(i + 1.0), u);
}

float noise(vec2 x) {
	vec2 i = floor(x);
	vec2 f = fract(x);
	float a = hash(i);
	float b = hash(i + vec2(1.0, 0.0));
	float c = hash(i + vec2(0.0, 1.0));
	float d = hash(i + vec2(1.0, 1.0));
	vec2 u = f * f * (3.0 - 2.0 * f);
	return mix(a, b, u.x) + (c - a) * u.y * (1.0 - u.x) + (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 tx = vec2(1.0) / iChannelResolution[0].xy;
	vec2 uv = fragCoord.xy * tx;

    int p00 = int(texture(iChannel0, uv + vec2(-tx.x, -tx.y)).x);
    int p01 = int(texture(iChannel0, uv + vec2(  0.0, -tx.y)).x);
    int p02 = int(texture(iChannel0, uv + vec2(+tx.x, -tx.y)).x);
    int p10 = int(texture(iChannel0, uv + vec2(-tx.x, 0.0)).x);
    int p11 = int(texture(iChannel0, uv).x);
    int p12 = int(texture(iChannel0, uv + vec2(+tx.x, 0.0)).x);
    int p20 = int(texture(iChannel0, uv + vec2(-tx.x, +tx.y)).x);
    int p21 = int(texture(iChannel0, uv + vec2(  0.0, +tx.y)).x);
    int p22 = int(texture(iChannel0, uv + vec2(+tx.x, +tx.y)).x);

    if (iTime < 0.25)
    {
        float n = noise(25.0 * (fragCoord.xy + iTime + float(p11)));
        fragColor = vec4(n < 0.5 ? 0.0 : 1.0);
        return;
    }

    int s = p00 + p01 + p02 + p10 + p12 + p20 + p21 + p22;

    int result = int((s == 3) || (s == 2) && (p11 != 0));

    fragColor = vec4(float(result));
}
