void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 texColor = texture(iChannel0, uv);
    vec4 rColor = texture(iChannel0, vec2(uv.x + 0.01, uv.y));
    vec4 lColor = texture(iChannel0, vec2(uv.x - 0.01, uv.y));
    vec4 tColor = texture(iChannel0, vec2(uv.x, uv.y + 0.01));
    vec4 bColor = texture(iChannel0, vec2(uv.x, uv.y - 0.01));
    vec3 norm;
    norm.z = 0.5;
    float dirToUse = 1.0 - texColor.x;
    norm.x = (lColor.x - rColor.x) * dirToUse;
    norm.y = (bColor.x - tColor.x) * dirToUse;
    //norm.x = pow(norm.x, 2.0);
    //norm.y = pow(norm.y, 2.0);

    norm = normalize(norm);
    texColor.y = texColor.x;
    texColor.z = texColor.x;
    vec3 campos = vec3(0.0, 0.0, 1.0);
    vec3 lightsrc = vec3(sin(iTime) + 0.5, 0.5, 1.0);
    vec3 pixpos = vec3(uv, 0.0);
    vec3 lightVec = lightsrc - pixpos;
    float dist = length(lightVec);
    lightVec = normalize(lightVec);
    dist /= 1.42;
    dist = pow(dist, 5.0);
    dist = 1.0 - dist;

    float normScalar = min(0.0, dot(lightVec, -norm));
    normScalar = pow(normScalar, 2.0);


    float diffScalar = dot(lightVec, campos);

    fragColor.x = normScalar;
    //fragColor.y = uv.y;
    fragColor.x = dist * diffScalar /* texColor*/ * normScalar;
    fragColor.y = fragColor.x;
    fragColor.z = fragColor.x;
}
