vec2 mainSound( in int samp, float i )
{ float t=fract(i/52.)*52.;
    float b=fract(t/4.)<5./6.?180.*acos(-1.):108.*acos(-1.);

    return 

        vec2( sin(b*t)*exp(-3.0*fract(t/2.)),sin(b*t)*exp(-5.0*fract(--t/2.)) )
    + 
        vec2( sin(b*floor(t/8.)*(t))*exp(-2.0*fract(t*3.+.5))
         + 
          clamp( vec2( sin(b*floor((t+1.8)/8.)*(t))*exp(-3.0*fract(t*1.5+.5))),.2,.8)
          );

}
