float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float calc_circle(float n, vec2 xy, vec2 offset)
{
	vec2 ixy = floor(xy) - offset;
	vec2 centre = ixy + 0.5;

	float r = 0.05 + 0.2*rand(ixy+100.0);
	centre += 0.25 + 0.5*rand(ixy);

	float angle = rand(ixy+50.0)+iTime*(rand(ixy+150.0)-0.5);
	centre.x += 0.25*sin(angle);
	centre.y += 0.25*cos(angle);

	vec2 d = xy - centre;
	float hsq = d.x*d.x + d.y*d.y;

	return n + 1.0 - smoothstep(r, r+0.01, hsq);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	uv.x = uv.x*5.0*iResolution.x/iResolution.y + 0.1*sin(1.0*iTime);
	uv.y = uv.y*5.0 - 0.6*iTime;

	float n = 0.0;

	n = calc_circle(n, uv, vec2(0.0, 0.0));
	n = calc_circle(n, uv, vec2(0.0, 1.0));
	n = calc_circle(n, uv, vec2(1.0, 0.0));
	n = calc_circle(n, uv, vec2(1.0, 1.0));


	uv = fragCoord.xy / iResolution.xy;

	uv.x = uv.x*6.0*iResolution.x/iResolution.y + 0.1*sin(1.2*iTime);
	uv.y = uv.y*6.0 - 0.5*iTime + 0.1*sin(3.0*iTime);

	n = calc_circle(n, uv, vec2(0.0, 0.0));
	n = calc_circle(n, uv, vec2(0.0, 1.0));
	n = calc_circle(n, uv, vec2(1.0, 0.0));
	n = calc_circle(n, uv, vec2(1.0, 1.0));

	uv = fragCoord.xy / iResolution.xy;

	uv.x = uv.x*8.0*iResolution.x/iResolution.y + 0.1*sin(1.3*iTime);
	uv.y = uv.y*8.0 - 0.4*iTime;

	n = calc_circle(n, uv, vec2(0.0, 0.0));
	n = calc_circle(n, uv, vec2(0.0, 1.0));
	n = calc_circle(n, uv, vec2(1.0, 0.0));
	n = calc_circle(n, uv, vec2(1.0, 1.0));


	fragColor = vec4(pow(0.15*n, 0.75), 0.0, 0.0, 1.0);

}
