mat2 rz2(float a) {
    float c = cos(a); 
    float s = sin(a);
    return mat2(c, -s, s, c);
}

float map(vec3 p) {
    p.xy *= rz2(iTime * .3);
    p.yz *= rz2(iTime * .4);

    float freq = texture(iChannel0, vec2(.02, .25)).r;
    p = mod(p + 1., 2.) - 1.;
    return length(p) - (.1 + .4*freq); // (mod(freq, .4) + .2) ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;

    vec3 ro=vec3(0.,0.,3.4);
    vec3 rd=normalize(vec3(uv, -1.));

    vec3 mp=ro;
    for (int i=0; i<50; ++i) {
     	float md = map(mp);
        if (md < .0001) break;
        mp += rd * md;
    }

    fragColor = vec4(length(mp - ro) * 0.1);
}
