void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
	U = (U+U - R ) / R.y; U.y -= .3;
    float l = length(U), a = atan(U.y,U.x), 
          k = 6.283 / (floor(l*4.)*R.y/8.);   
//        k = 6.283 / (floor(exp2(floor(log2(l*6.))))*R.y/8.);  // exp 
//        k = 6.283 / (floor(pow(l,1.5)*6.)*R.y/8.);            // darker

    O = vec4(1) - smoothstep(3./R.y, 0., abs( mod(a,k) -.5*k ) *l );
//  O = vec4(1) - smoothstep(3./R.y, 0., abs( mod(a+.5*k,k) -.5*k ) *l );               // aligned
//  O = vec4(1) - smoothstep(3./R.y, 0., abs( mod(a,k) -.5*k*(1.+.1*sin(60.*l)) ) *l ); // wooble

                                                                // --- colored variants
//  O = .15*vec4(6,3,1,1)/l - smoothstep(3./R.y, 0., abs(mod(a,k)-.5*k)*l);
//  O = 1. - smoothstep(3./R.y, 0., abs(mod(a,k)-.5*k)*l) * vec4(0,1,2,0)*l;
//  O = smoothstep(3./R.y, 0., abs(mod(a,k)-.5*k)*l) * min( vec4(2,1,.5,0)/l, 1.);
}
