void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // RGBA
    vec4 color = vec4(0.5, 1.0, 0.5, 1.0);
    // color.gr = vec2(0.1, 0.5);

    // pixel position
	vec2 pixel = fragCoord.xy / iResolution.xy;

    //color.r = pixel.y;

    // center origin point 
    pixel = (pixel - 0.5) * 2.0;

    // aspect ratio
    pixel.x *= iResolution.x / iResolution.y;

    // time elapsed
    float time = iTime;

    //pixel -= vec2(.5, 0.);

    // tr√®fle
    float r = sqrt(dot(pixel, pixel));
    float a = atan(pixel.y, pixel.x) + time * 0.1;
    float s = 0.5 + 0.5 * sin(3.0 * a);
    float t = 0.15 + 0.35 * pow(s, 0.3);
    t += 0.1 * pow(0.5 + 0.5 * cos(6.0 * a), 0.5);
    float h = r/t;
    float f = 0.0;

    if(h < 1.0) f = 1.0;

    color.rgb = mix(vec3(1.0), vec3(0.5 * h, 0.5 + 0.5 * h, 0.0), f);

	fragColor = color;
}
