void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float t = iTime;
    float bpm = 1.0 / 60.0 * 88.0;
    float pi = 3.14159265359;

    float ts = 1.0 / 32.0;
    float t0 = texture(iChannel0, vec2(ts * 0.0, 0.25)).x;
    float t1 = texture(iChannel0, vec2(ts * 3.0, 0.25)).x;
    float t2 = texture(iChannel0, vec2(ts * 4.0, 0.25)).x;
    float t3 = texture(iChannel0, vec2(ts * 29.0, 0.25)).x;

    t1 = max(0.0, t1 - 0.67) / 0.33 * 0.1;
    t2 = max(0.0, t2 - 0.70) / 0.30 * 0.1;
    t3 = max(0.0, t3 - 0.2) * 1.5;
    t3 = pow(t3, 2.0);

    float t12 = pow(t1 + t2, 0.8) * 0.5;

    vec3 c = vec3(0.0);

    vec2 nnuv = vec2(nuv.x * sin(nuv.x));

    c.x = fract(nnuv.x * tanh(-nnuv.x * (20.0 - (22.5 * t12)))) * max(0.0, sin(t * bpm * pi * 300.0)) * 0.25 * t0 * 1.0 - uv.y * abs(nnuv.x * 0.1);

    c += vec3((t0 + t1 + t2 + t3) * 0.05);

    c.x += pow(t0 - 0.0, 2.0) * 0.25 * pow(uv.y, 2.0);
    c.y += pow(t0 - 0.3, 2.0) * 0.25 * pow(uv.y, 2.0);
    c.z += pow(t3 - 0.0, 2.0) * 2.5 * pow(uv.y, 3.0);

    c.xyz -= fract(nnuv.y * (100.0 + t3) * abs(nuv.x * 100.0));
    c.xyz += t3 * uv.y;

    c.xy -= abs(sin(nnuv.x * 200.0)) * 1.2 * t3;

    c.z -= max(0.0, abs(1.0 - nuv.x) * 200.0 * t12);

    c.xyz += nuv.y * 0.3;

    c.y *= max(0.0, c.z * abs(nnuv.x) * 2.5 * t3);

    c.xyz += sin(nnuv.y) * 0.1 * t2;

    fragColor.xyz = c;
}
