{
    vec2 center = vec2(iResolution.x * 0.5, iResolution.y * 0.5);
    vec2 direction = fragCoord.xy - center.xy;
    float r = 40.0;

    float dist = sqrt( direction.x * direction.x + direction.y * direction.y );
	vec2 uv = fragCoord.xy / iResolution.xy;

    if (dist < r) {
