float map (vec3 p) {

    vec3 q = fract(p) * 2.0 - 1.0;

    return length(q) - 0.25;

}

float trace(vec3 o, vec3 r) {
    float t = 0.0;
    for(int i = 0; i < 32; ++i) {
       vec3 p = o+r*t;
        float d = map(p);
        t += d*1.0;
    }
    return t;
}

void mainImage ( out vec4 fragColor, in vec2 fragCoord) {

    vec4 col = vec4(0,0,0,1);
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -= 0.5;

    if(length(uv) < 0.15 && uv.x > -0.05) {

        col = mix(vec4(0.52,0.4,0,1), vec4(0.), length(uv));
        col = mix(col, vec4(0), smoothstep(0.0, 0.2, uv.y));
        col = mix(col, vec4(0), smoothstep(0.0, -0.2, uv.y));
    }
    else {

    uv = uv*2.0-0.4;
    uv.x = iResolution.x/iResolution.y;
    vec3 r = normalize(vec3(uv, 0.8));
    float time = iTime;
    vec3 o = vec3(0.0, 0.0, time);
    float t = trace(o,r);
    float fog = 1.0/ (1.0 + t * t * 0.1);
    vec3 fc =  vec3(fog);

        col = vec4(fc, 2.0);
    }
    fragColor = col;
}
