float luminosity( vec4 aColor )
{
    return 0.21 * aColor.r + 0.72 * aColor.g + 0.07 * aColor.b;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;//Condensing this into one line
    vec4 tex0Color = texture(iChannel0,xy);//Get the pixel at xy from iChannel0
    vec4 tex1Color = texture(iChannel1,xy);//Get the pixel at xy from iChannel0

    vec4 texColor = tex0Color;

    //float bwVal = luminosity(texColor);
    // texColor.rgb = ((texColor.rgb - bwVal ) *  abs(sin(iTime))) + bwVal;  

    vec4 backgroundColor = vec4(13.0/255.0,161.0/255.0,37.0/255.0,1.0);
	float difference = distance(texColor,backgroundColor);
	if( difference < 0.4 ) {
		texColor = tex1Color;
    } else {
        float bwVal = luminosity(tex0Color);
        texColor.r = bwVal;
        texColor.g = bwVal;
        texColor.b = bwVal;
    }


    fragColor = texColor;
}
