vec2 sine_swath(float seed, float factor, float factor2, vec2 uv) {
    return (vec2(sin(seed/15.0 + iTime * (0.1 / factor)), cos(seed/15.0 + iTime * (0.1 / factor))) * factor2) + uv; 
}

vec2 cosine_swath(float seed, float factor, float factor2, vec2 uv) {
    return (vec2(cos(seed/15.0 + iTime * (0.1 / factor)), sin(seed/15.0 + iTime * (0.1 / factor))) * factor2) + uv; 
}

vec4 samplefunc(vec2 coord) {
    vec2 uva = coord.xy / iResolution.xy;

    vec2 uv = sine_swath(coord.x + coord.y, 0.05, 0.005, uva);
    uv = cosine_swath(coord.x + coord.y, 0.05, 0.005, uva);

    vec4 color = texture(iChannel0, uv); 
    return color;
}

vec4 blur(vec2 coord, float dim) {
    vec3 color = vec3(0.0, 0.0, 0.0);
    for (float y = (dim - 1.0) / -2.0; y <= (dim - 1.0) / 2.0; y++) {
        for (float x = (dim - 1.0) / -2.0; x <= (dim - 1.0) / 2.0; x++) {
            color += samplefunc(vec2(coord.x + x, coord.y + y)).rgb;    
        }
    }
    color /= dim * dim;
    color.r *= 0.6;
    color.g *= 0.8;
    color.b *= 2.5;
    return vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 color = blur(fragCoord, 9.0);
    fragColor = color;
}
