void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 nuv = uv * 2.0 - 1.0;
	float t = iTime;
    float bpm = 1.0 / 60.0 * 121.0;
    float bt = t * bpm / 3.14159265359 * 2.0;

    float mg = texture(iChannel0, vec2(0.0, 0.25)).x;
    float mbeat = texture(iChannel0, vec2(0.8, 0.25)).x;

    mg = pow(mg - 0.1, 0.3);
    mbeat = pow(mbeat, 12.0);

    float r0 = nuv.x * nuv.y;
    float d0 = 2.0 * pow(tanh(r0 * 2.0 - sin(mbeat + bt * 8.0)), -1.0);

    mat2 rot1 = mat2(cos(bt), -sin(bt), sin(bt), cos(bt));
    float r1 = abs((nuv * rot1).x * (nuv * rot1).y);
    float d1 = 2.0 * pow(tanh(r1 * 2.0), -1.0);

    float c0 = abs(d0) * 0.15 + sin(bt * 2.0) * 0.1 * mbeat;
    float c1 = d1 * 0.01 * abs(sin(nuv.y * nuv.x * 200.0 * mg) + dot(uv.x, uv.y) * sin(bt * 4.0));

    fragColor.x = 0.1;
    fragColor.y = pow(c1, 0.9) * (0.2 + pow(mbeat, 0.5) * 20.0) + c1 * -abs(length(nuv)) * 4.0 + r1 * abs(sin(bt));
    fragColor.z = c0;

    fragColor.xyz += vec3(mg) * 0.2 * (pow(length(nuv), 4.4));
}
