{
    const float PI = 3.1415926535897932384626433832795;
	vec2 uv = fragCoord.xy / iResolution.xy;

    float a = 0.5;
    float b = 0.5;
    float c = 0.5;
    float d = 0.0;
       vec3 color; 



    float i = (uv.y*uv.x)+ iTime;
    a = 0.5;
    b = 0.5;
    c = 1.0;
    d = 0.00;

        color.r = a + b * cos((2.0 * PI) * (c * i + d));
    a = 0.5;
    b = 0.5;
    c = 1.0;
    d = 0.330;
        color.g = a + b * cos((2.0 * PI) * (c * i + d));

