#define STEPS 1000

float f(float x, float y) {
    return 100.0 * sin(0.1*x) * sin(0.1*y) * sin(iTime) - 125.0;
}

void mainImage(out vec4 c, in vec2 w) {
    vec4 p = vec4(w/iResolution.xy,1,1) - 0.5;
    vec4 d = p * 0.5;
    p.z += 20.*iTime;
    d.y -= 0.2;

    for(int i = 0; i < STEPS; i++) {
        c = vec4(vec3(i)/float(STEPS), 1);
        if(f(p.x,p.z) > p.y) break;
        p += d;
    }
}
