void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    vec2 mouse = iMouse.xy / iResolution.xy;

    mouse.x *= iResolution.x / iResolution.y;

    float brush_radius = .053;
    vec4 brush_color = vec4(abs(cos(iTime)), .5, .7, 1.);

    float dist = clamp(brush_radius-length(uv-mouse)/brush_radius, 0., 1.);

    dist = clamp(brush_radius-length(uv-mouse), 0., 1.);
    dist = sqrt(dist);

    vec4 col = vec4(dist)*brush_color;

    fragColor = vec4(col) + texture(iChannel0, fragCoord/iResolution.xy);
}
