#define KEY_1 49
#define KEY_2 50

bool keyToggle(int ascii)
{
    return (texture(iChannel2,vec2((.5+float(ascii))/256.,0.75)).x > 0.1);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    if (keyToggle(KEY_1))
    {
    	vec2 t = texture(iChannel1, uv).gb;
    	//uv.y += ((t.x/t.y)+iTime*1.2)*.04;
        uv.xy += ((t.xy-.5)*.04);
        uv.y += iTime*.08 + .01*sin(iTime*.4);

    }
    else
    {

        vec2 t = texture(iChannel1, uv).gb;
        float noise = (t.x/t.y)+
            			iTime*-.025+
            			.01*sin(iTime);
        uv += vec2(noise*.4);
    }

    fragColor = vec4(texture(iChannel0, uv));
}
