#define SPEED 0.7
void mainImage( out vec4 o, in vec2 p )
{

    vec3 R = iResolution;
    float T = iTime*SPEED;
    float fT = fract(T);
    p=(p-R.xy*.5)/min(R.x,R.y);
    p*=mat2(cos(T),sin(T/2.),-sin(T/2.),cos(T));

    o=texture(iChannel0,vec2(length(p)));
    o.r*=mix(.9,1.1,texture(iChannel1,p*sin(T*2.)).r);
    o*=mix(1.2,1.6,texture(iChannel1,p).r);
}
