//Easing from gslify
float exponentialIn(float t) {return t == 0.0 ? t : pow(2.0, 10.0 * (t - 1.0));}
float exponentialOut(float t) {return t == 1.0 ? t : 1.0 - pow(2.0, -10.0 * t);}

vec3 color(vec3 col, vec2 center, float radius, vec2 uv){
    float rx = iResolution.x/iResolution.y;
    float d = distance(vec2(center.x*rx,center.y),uv);
    float v = smoothstep( 0., radius, d );
    return col * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //base
	vec2 uv = fragCoord.xy/iResolution.yy;
	vec3 col = vec3(.95);
    vec3 col2 = vec3(0.1);
    float rx = iResolution.x/iResolution.y;

    //push somecolor on it
    col2 += 1.1*color(vec3(.3,abs(sin(iTime))*.4,.5),vec2(.5,.6),.6, uv);
    col2 += 1.1*color(vec3(abs(cos(iTime/3.))*.5,.2,.2), vec2(sin(iTime/2.)*.3+.5,cos(iTime)*.5+.6), 1., uv);
    col2 += 1.8*color(vec3(.45,abs(cos(iTime/3.))*.05+.4,.15), vec2(.7,.5), 1.2, uv);
    col2 += .5*color(vec3(.2,.2,.15), vec2(cos(iTime/2.),.7), 1.2, uv);
    col2 += .8*color(vec3(.25,.25,.25+sin(iTime/4.)*.2), vec2(cos(iTime/2.)+.3,.5), 1.2, uv);

    //basic
    float d = distance(vec2(.5*rx,.5+sin(iTime/4.)*.05),uv);
    float v = smoothstep( .3, 1., d );
    float e = exponentialOut(v);
    e = exponentialOut(e);
    e = e*e*e*e*e*e*e*e;
    //*
    e = exponentialOut(e);
    e = exponentialOut(e);
    e = exponentialOut(e);
	//*/
    col *= e;
    col += col2*.8;
    col *= col+.1;
    col = min(vec3(1.),col);
	fragColor = vec4(col,1.0);    
}
