void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = mix(vec4(0), vec4(1), uv.x < 0.85 ? 0.0 : (uv.x - 0.85) * 6.66);
    fragColor = mix(fragColor, vec4(1), uv.x < 0.15 ? (0.15 - uv.x) * 6.66 : 0.0);
    fragColor = mix(fragColor, vec4(1), uv.y < 0.85 ? 0.0 : (uv.y - 0.85) * 6.66);
    fragColor = mix(fragColor, vec4(1), uv.y < 0.15 ? (0.15 - uv.y) * 6.66 : 0.0);

    //fragColor = mix(vec4(0), vec4(1), pow((uv.x - 0.5) * 2.0, 10.0));
    //fragColor = mix(fragColor, vec4(1), pow((uv.y - 0.5) * 2.0, 10.0));
}
