void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv2 = vec2(uv.x + sin(uv.y + iTime), uv.y + sin(uv.x + iTime * 0.3));
    vec2 uv3 = vec2(uv2.x + sin(uv2.y + iTime * 0.2), uv2.y + sin(uv2.x + iTime * 0.5));
    vec2 uv4 = vec2(uv3.x + sin(uv3.y + iTime * 0.1), uv3.y + sin(uv3.x + iTime * 0.15));

    fragColor =
        vec4(1,0.5,0.2,1) * max(0.0,pow(-sin(uv2.y * 34.0), 20.0)) +
        vec4(-1,2,0.2,1) * max(0.0,pow(-sin(uv3.y * 34.0), 20.0)) +
        vec4(1, 0.2, 0.5, 1) * max(0.0,pow(-sin(uv4.y * 34.0), 20.0)) +
	vec4(0.2,0.1,0.2,1); 
}
