void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor =texture(iChannel0, uv)*.875+texture(iChannel1, uv+iDate.w*sin(cos(iDate.w)))*.125;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float l = (uv.y-(.75+(sin(iDate.w)/4.)));
    if (l>.0) l = 0.;
    uv+=-l*iDate.w;
    uv/=l;
	fragColor =texture(iChannel0, uv)*l*2.+vec4(0.25, 0, 0,0)*(1.0-2.*l);
}
