mat2 rot(float th) { float cs = cos(th), si = sin(th); return mat2(cs, -si, si, cs); }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = 0.0;
	vec2 uv = (fragCoord - iResolution.xy*0.5) / iResolution.y * 2.0;
    vec3 rd = normalize(vec3(uv, 1.));
    rd.yz *= rot(angle);
    float sDist = max(max(abs(rd.x), abs(rd.y)), 1e-16);
    sDist = 1. / (sDist);
    vec3 sp = rd*sDist;
    vec3 sn =  normalize(-sign(sp)*vec3(abs(rd.x)>abs(rd.y) ? vec2(1., 0.) : vec2(0., 1.), 0.));
    float U; 
    float V = sp.z;//atan(sp.z)*0.5)+0.5;//sp.z;
    if(sn.x==0.0){
       // V = uv.y;
        U = (sp.x / 2.0) + 0.5;
    }else if (abs(sn.x)==1.0) {
        //V = uv.x;
		U = (sp.y / 2.0) + 0.5;
    }



    fragColor = vec4(texture(iChannel0, vec2(1.0-V,U)).xyz,1.0);
}
