void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

	///* V2
    vec2 pivot;

    vec2 coord;
    vec2 mouse;

    vec2 direction;

    coord = fragCoord;
    pivot = iResolution.xy * 0.5;
    mouse = iMouse.xy;

    pivot.x += sin(0.5*iTime) * iResolution.x * 0.5;

    float distPivot = length(coord-pivot);
    float distMouse = length(coord-mouse);

    float col1 = 0.0, col2 = 0.0;
	//*/

    // VARIA EL COLOR
    col1 += sqrt( 10.0 / ( distPivot  ) );
    col2 += sqrt( 10.0 / ( distMouse  ) );

    // ESQUEMA DE COLOR 1
    float color = col1 + col2;
    fragColor = vec4(1.0) * color;

	// ESQUEMA DE COLOR 2
    vec4 color1 = vec4(1.0,0.0,0.0,1.0);
    vec4 color2 = vec4(1.0,1.0,0.0,1.0);

    fragColor = color1 * col1 + color2 * col2;

}
